import api from './api';

/**
 * EvacuationAreaService
 * Handles all API calls for evacuation areas matching Laravel routes
 */
export default {
  /**
   * Get all evacuation areas
   * GET /api/evacuation-areas
   */
  async getAll() {
    const response = await api.get('/evacuation-areas');
    return response.data;
  },

  /**
   * Get single evacuation area with details
   * GET /api/evacuation-areas/{id}
   */
  async getById(id) {
    const response = await api.get(`/evacuation-areas/${id}`);
    return response.data;
  },

  /**
   * Create new evacuation area
   * POST /api/evacuation-areas
   */
  async create(data) {
    const response = await api.post('/evacuation-areas', data);
    return response.data;
  },

  /**
   * Update evacuation area
   * PUT /api/evacuation-areas/{id}
   */
  async update(id, data) {
    const response = await api.put(`/evacuation-areas/${id}`, data);
    return response.data;
  },

  /**
   * Delete evacuation area
   * DELETE /api/evacuation-areas/{id}
   */
  async delete(id) {
    const response = await api.delete(`/evacuation-areas/${id}`);
    return response.data;
  },

  /**
   * Get nearest evacuation areas based on coordinates
   * GET /api/evacuation-areas/nearest?latitude=&longitude=
   */
  async getNearest(latitude, longitude) {
    const response = await api.get('/evacuation-areas/nearest', {
      params: { latitude, longitude }
    });
    return response.data;
  },

  /**
   * Get recommended evacuation areas with prescriptive analytics
   * GET /api/evacuation-areas/recommend?latitude=&longitude=&predicted=
   */
  async getRecommended(latitude, longitude, predicted = 29.41) {
    const response = await api.get('/evacuation-areas/recommend', {
      params: { latitude, longitude, predicted }
    });
    return response.data;
  },

  /**
   * Register family at evacuation area (go endpoint)
   * POST /api/evacuation-areas/{id}/go
   */
  async registerFamilyAtArea(id, familyData) {
    const response = await api.post(`/evacuation-areas/${id}/go`, familyData);
    return response.data;
  },
};
