import { createApp } from 'vue'
import App from './App.vue'
import router from './router'
import axios from 'axios'
import './index.css';
import 'font-awesome/css/font-awesome.min.css';
import 'leaflet/dist/leaflet.css';

import VueSweetalert2 from 'vue-sweetalert2';
import 'sweetalert2/dist/sweetalert2.min.css';

import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import 'bootstrap-icons/font/bootstrap-icons.css';

const app = createApp(App).use(router).use(VueSweetalert2);

// Prefer an explicit env var; default to port 8001 (System-E uses 8001 in this workspace)
const baseApi = (process.env.VUE_APP_ADMIN_API_URL || 'http://127.0.0.1:8001/api').replace(/\/$/, '');
app.provide('baseUrl', baseApi);

// Configure axios default base URL so components can use relative paths
axios.defaults.baseURL = baseApi;

// If a resident token is provided via env (VUE_APP_RESIDENT_API_TOKEN), set it on axios
if (process.env.VUE_APP_RESIDENT_API_TOKEN) {
  axios.defaults.headers.common['Authorization'] = `Bearer ${process.env.VUE_APP_RESIDENT_API_TOKEN}`;
}

// set token on start if exists
const token = localStorage.getItem('token');
if (token) {
	axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
	// lazy import AuthService to set header
	import('./services/AuthService').then(({ default: AuthService }) => AuthService.setToken(token));
}

// Add axios interceptor to set token on every request
axios.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

app.mount('#app');
