import { createApp } from 'vue'
import App from './App.vue'
import router from './router'
import bootstrap from 'bootstrap';
import './index.css';
import 'font-awesome/css/font-awesome.min.css';

import VueSweetalert2 from 'vue-sweetalert2';
import 'sweetalert2/dist/sweetalert2.min.css';

import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import 'bootstrap-icons/font/bootstrap-icons.css';

const app = createApp(App).use(router).use(VueSweetalert2).use(bootstrap);

const baseApi = 'http://127.0.0.1:8000/api';
app.provide('baseUrl', baseApi);

// set token on start if exists
const token = localStorage.getItem('token');
if (token) {
	// lazy import AuthService to set header
	import('./services/AuthService').then(({ default: AuthService }) => AuthService.setToken(token));
}

app.mount('#app');
