/**
 * EvacuationAreaService.js
 * Service to manage API calls for evacuation areas
 * Matches Laravel EvacuationAreaController endpoints
 */

import api from './api';

const EvacuationAreaService = {
  /**
   * Get all evacuation areas
   * GET /api/evacuation-areas
   * @returns {Promise<Array>}
   */
  async getAll() {
    try {
      const response = await api.get('/evacuation-areas');
      return response.data;
    } catch (error) {
      console.error('Error fetching evacuation areas:', error);
      throw error;
    }
  },

  /**
   * Get a single evacuation area by ID
   * GET /api/evacuation-areas/{id}?latitude=&longitude=
   * @param {number} id - Evacuation area ID
   * @param {number} latitude - Optional user latitude for distance calculation
   * @param {number} longitude - Optional user longitude for distance calculation
   * @returns {Promise<Object>}
   */
  async getById(id, latitude = null, longitude = null) {
    try {
      const params = {};
      if (latitude !== null && longitude !== null) {
        params.latitude = latitude;
        params.longitude = longitude;
      }
      const response = await api.get(`/evacuation-areas/${id}`, { params });
      return response.data;
    } catch (error) {
      console.error(`Error fetching evacuation area ${id}:`, error);
      throw error;
    }
  },

  /**
   * Create a new evacuation area
   * POST /api/evacuation-areas
   * @param {Object} data - Evacuation area data
   * @returns {Promise<Object>}
   */
  async create(data) {
    try {
      const response = await api.post('/evacuation-areas', data);
      return response.data;
    } catch (error) {
      console.error('Error creating evacuation area:', error);
      throw error;
    }
  },

  /**
   * Update an evacuation area
   * PUT /api/evacuation-areas/{id}
   * @param {number} id - Evacuation area ID
   * @param {Object} data - Updated evacuation area data
   * @returns {Promise<Object>}
   */
  async update(id, data) {
    try {
      const response = await api.put(`/evacuation-areas/${id}`, data);
      return response.data;
    } catch (error) {
      console.error(`Error updating evacuation area ${id}:`, error);
      throw error;
    }
  },

  /**
   * Delete an evacuation area
   * DELETE /api/evacuation-areas/{id}
   * @param {number} id - Evacuation area ID
   * @returns {Promise<Object>}
   */
  async delete(id) {
    try {
      const response = await api.delete(`/evacuation-areas/${id}`);
      return response.data;
    } catch (error) {
      console.error(`Error deleting evacuation area ${id}:`, error);
      throw error;
    }
  },

  /**
   * Get nearest evacuation areas to user location
   * GET /api/evacuation-areas/nearest?latitude=&longitude=
   * @param {number} latitude - User latitude
   * @param {number} longitude - User longitude
   * @returns {Promise<Array>}
   */
  async getNearestAreas(latitude, longitude) {
    try {
      const response = await api.get('/evacuation-areas/nearest', {
        params: { latitude, longitude }
      });
      return response.data;
    } catch (error) {
      console.error('Error fetching nearest evacuation areas:', error);
      throw error;
    }
  },

  /**
   * Get recommended evacuation areas
   * GET /api/evacuation-areas/recommend?latitude=&longitude=&predicted=
   * @param {number} latitude - User latitude
   * @param {number} longitude - User longitude
   * @param {number} predicted - Predicted number of evacuees
   * @returns {Promise<Object>} Prescriptive recommendations with allocations
   */
  async getRecommendedAreas(latitude, longitude, predicted = 29.41) {
    try {
      const response = await api.get('/evacuation-areas/recommend', {
        params: { latitude, longitude, predicted }
      });
      return response.data;
    } catch (error) {
      console.error('Error fetching recommended evacuation areas:', error);
      throw error;
    }
  },

  /**
   * Register a family to an evacuation area (evacuee check-in)
   * POST /api/evacuation-areas/{id}/go
   * @param {number} id - Evacuation area ID
   * @param {Object} familyData - Family registration data
   * @returns {Promise<Object>}
   */
