    try {
      const response = await api.post(`/evacuation-areas/${id}/go`, familyData);
      return response.data;
    } catch (error) {
      console.error(`Error registering family to evacuation area ${id}:`, error);
      throw error;
    }
  }
};

export default EvacuationAreaService;
